import React, { useRef, useEffect, useState } from 'react';

const ImageToggleOnScroll = ({ primaryImage, secondaryImage }) => {
	const imageRef = useRef(null);

	return (
		<div>
			<img src={primaryImage} alt='' ref={imageRef} />
		</div>
	);
};

export default ImageToggleOnScroll;
